// models/user.js
const { Model, DataTypes } = require('sequelize');
const sequelize = require('../utils/dbConnector');
const EmailValidation = require('./emailValidation')

class User extends Model { }

User.init({
    // Unique, autogenerated id
    id: {
        type: DataTypes.INTEGER,
        autoIncrement: true,
        primaryKey: true,
    },
    // Email address
    email: {
        type: DataTypes.STRING,
        allowNull: false,
        unique: true,
    },
    // Hashed password
    passwordHash: {
        type: DataTypes.STRING,
        allowNull: false,
    },
    // Last known login time
    lastLogin: {
        type: DataTypes.DATE,
    },
    // Last known request time
    lastActivity: {
        type: DataTypes.DATE,
        allowNull: false,
        defaultValue: DataTypes.NOW,
    },
}, {
    sequelize,
    modelName: 'User',
});

User.associate = (models) => {
    User.hasOne(models.EmailValidation);
};

module.exports = User;